import React,{useState} from 'react';
import './App.css';
import { Login } from './Login';

function App() {

  const [currentForm,setCurrentForm]=useState('login')

  const toggleForm =(formName)=>{
    setCurrentForm(formName);
  }


  return (
    <div className="App">
         <Login onFormSwitch={toggleForm} />
    </div>
  );
}

export default App;
