import React, { useState } from "react";
import "./styles/login.css"
// import { FcGoogle } from 'react-icons/fc';

const style = { color: "white", fontSize: "1.5em" ,background:"white",border: "1px solid black" }

export const Login = (props) => {
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(email);
    }

    return (
        <div>
            <h5>Welcome to Systempackage</h5>
        <div className="auth-form-container">
            <form className="login-form" onSubmit={handleSubmit}>
                <input className="EMAIL"  value={email} onChange={(e) => setEmail(e.target.value)}type="email" placeholder="Email" id="email" name="email" />
                <input className="PASSWORD" value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="Password" id="password" name="password" />
                <button className="link-btn"  onClick={() => props.onFormSwitch('register')}>Don't have an account? Register here.</button>
                <button className="SUBMIT" type="submit">Submit</button>
                <p className="OR">OR</p>
                <button className="GOOGLE"  type="submit"> Login with Google</button>
            </form>
        </div>
        </div>
        
    )
}
